 $(document).ready(function() {
     var ua = window.navigator.userAgent;
     var msie = ua.indexOf("MSIE ");
     if (msie > 0 || !!navigator.userAgent.match(/Trident.*rv\:11\./)) // If Internet Explorer, return version number
     {
         init.setSize();
     }
 });
 var init = {
     setSize: function() {
         $('.wrapper .background').css('height', $(window).height());
         $('.wrapper .background').css('width', $(window).width());

     }
 }

 // leaderboard button
 jQuery(".read_more").click(function(event) {

     var isVisible = jQuery(this).parents('.leaderboard').find(".a2").is(":visible");
     if (!isVisible) {
         for (i = 0; i < 25; i++) {
             var company_lists = $('div.a2').html();
             var company_item = '\
							<div class="board-outer">\
					            <div class="company">\
					                <div class="logo">\
					                    <img src="image/blackround.png">\
					                </div>\
					                <div class="company-content">\
					                    <h2>Company Name</h2>\
					                    <div class="progress">\
					                        <div class="progress-bar" role="progressbar" aria-valuenow="25" aria-valuemin="0" aria-valuemax="100" style="width:25%">\
					                        </div>\
					                    </div>\
					                    <span>0 Rides</span>\
					                </div>\
					            </div>\
					        </div>\
                		'
             $('div.a2').html(company_lists + company_item);
         }
         jQuery(this).parents('.leaderboard').find(".a2").slideDown();
         jQuery(this).text("View Less");
         $('div.slimscroll').slimscroll({
             height: '840px',
             allowPageScroll: true,
             alwaysVisible: true
         });

     } else {
         $('div.a2').html('');
         jQuery(this).parents('.leaderboard').find(".a2").slideUp();
         jQuery(this).text("View More");
         $('div.slimscroll').slimscroll({
             height: '500px',
         });

     }

 });

